import * as pulumi from "@pulumi/pulumi";
import * as sakuracloud from "@pulumi/sakuracloud";

const foobarServer = new sakuracloud.Server("foobarServer", {networkInterfaces: [{
    upstream: "shared",
}]});
const foobarProxyLB = new sakuracloud.ProxyLB("foobarProxyLB", {
    plan: 100,
    vipFailover: true,
    stickySession: true,
    gzip: true,
    proxyProtocol: true,
    timeout: 10,
    region: "is1",
    healthCheck: {
        protocol: "http",
        delayLoop: 10,
        hostHeader: "example.com",
        path: "/",
    },
    sorryServer: {
        ipAddress: "192.0.2.1",
        port: 80,
    },
    syslog: {
        server: "192.0.2.1",
        port: 514,
    },
    bindPorts: [{
        proxyMode: "http",
        port: 80,
        responseHeaders: [{
            header: "Cache-Control",
            value: "public, max-age=10",
        }],
    }],
    servers: [{
        ipAddress: foobarServer.ipAddress,
        port: 80,
        group: "group1",
    }],
    rules: [
        {
            action: "forward",
            host: "www.example.com",
            path: "/",
            group: "group1",
        },
        {
            action: "redirect",
            host: "www2.example.com",
            path: "/",
            group: "group1",
            redirectLocation: "https://redirect.example.com",
            redirectStatusCode: "301",
        },
        {
            action: "fixed",
            host: "www3.example.com",
            path: "/",
            group: "group1",
            fixedStatusCode: "200",
            fixedContentType: "text/plain",
            fixedMessageBody: "body",
        },
    ],
    description: "description",
